void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 vc = (fragCoord - iResolution.xy*.5)/iResolution.y;

    vec4 color  = vec4(uv,0.5+0.5*sin(iGlobalTime),1.);

    color*=1.-smoothstep(.3,.4,length(vc));

	vec2 pos = uv*2.-1.;

	color *= abs((sin(pos.x + sin(pos.y + iGlobalTime) *.5) * 10.0));

	fragColor = color;

}
