void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 ps = 1.0/iResolution.xy;
    vec2 uv = fragCoord.xy * ps;
    float dx = texture2D(iChannel0,uv+vec2(ps.x,0.0)).x-texture2D(iChannel0,uv+vec2(-ps.x,0.)).x;
    float dy = texture2D(iChannel0,uv+vec2(0.0,ps.y)).x-texture2D(iChannel0,uv+vec2(0.0,-ps.y)).x;
    float sc = 4.0;
	fragColor = vec4(textureCube(iChannel1,normalize(vec3(sin(sc*dx),cos(sc*dx)*cos(sc*dy),sin(sc*dy)))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ps = 1.0/iResolution.xy;
   	vec2 uv = fragCoord.xy * ps;

    float h = texture2D(iChannel0,uv).x-0.5;
    float v = (texture2D(iChannel0,uv).y-0.5);

    float hu = (texture2D(iChannel0,uv+vec2(0.0,-ps.y)).x-0.5)-h;
    float hd = (texture2D(iChannel0,uv+vec2(0.0,ps.y)).x-0.5)-h;
    float hl = (texture2D(iChannel0,uv+vec2(-ps.x,0.0)).x-0.5)-h;
    float hr = (texture2D(iChannel0,uv+vec2(+ps.x,0.0)).x-0.5)-h;

    v -= 0.1*h;
    v += (hu+hd+hl+hr)*.4;
    v *= 0.98;
    h += v;
    if (mod(iGlobalTime,1.0)<.05) {
        float amp = 0.5+0.5*sin(iGlobalTime*0.01);
        float dropsize = 12.0+10.0*sin(iGlobalTime*153.6);
        vec2 off = iResolution.xy * vec2(0.5+0.5*sin(floor(iGlobalTime*5.)*17.541),0.5+0.5*sin(floor(iGlobalTime*5.)*9.327));
        if (length(fragCoord-off)<dropsize) h=-0.5*amp*(1.0-length(fragCoord-off)/dropsize);
    }
    if (iMouse.x!=.0) {
        float amp = 1.0;
        float dropsize = 10.0;
        vec2 off = iMouse.xy;
        if (length(fragCoord-off)<dropsize) h=-0.5*amp*(1.0-length(fragCoord-off)/dropsize);        

    }
    if (iFrame==0) {
        h = 0.0;
        v = 0.0;
    }
    //h = clamp(h,-0.1,0.1);
    v = clamp(v,-0.5,0.5);
    fragColor = vec4(h+0.5,v+0.5,0.,0.);
}
