float hash( float n ) { return fract(sin(n)*43758.5453); }

vec2 mainSound( float time )
{
    float i = floor( time );                // interval
    float n = floor( 24.0*hash( i ) );      // note
    float f = 220.0 * pow( 2.0, n/12.0 );   // frequency

    // avoid click
    f = floor( f );

    return vec2( sin(6.2831*f*time) );
}
