vec2 rotate(vec2 v, float a){
	float t = atan(v.y,v.x)+a;
    float d = length(v);
    v.x = cos(t)*d;
    v.y = sin(t)*d;
    return v;
}

vec2 kale(vec2 uv, float angle, float base, float spin) {
	float a = atan(uv.y,uv.x)+spin;
	float d = length(uv);
	a = mod(a,angle*2.0);
	a = abs(a-angle);
	uv.x = sin(a+base)*d;
	uv.y = cos(a+base)*d;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float i = iGlobalTime;
	vec2 uv = fragCoord.xy / iResolution.xy*4.-2.0;
    float d = length(uv);
    uv = rotate(uv,d+sin(i));
    uv = kale(uv,3.14/6.,0.2,-i);
    i+=sin(uv.x*uv.y);
    vec4 c = vec4(sin(i*10.*3.14+vec3(1.0,1.0-d*.25,1.0-d*.5))*.5+.5, 1.0);
	fragColor = c;
}

vec2 mainSound(float time)
{
    vec2 g = vec2(1.0);
    float freq = 100.0;//+sin(time)*5.;
    vec2 f = vec2(1.0);
    f.x = sin(6.2831*freq*time+time);
    f.y = cos(6.2831*freq*time+time);
    float m = mod(time*10.0,2.0)-1.0;
	f*= pow(cos(m*3.141/2.),5.0);
    return f+f;
}
