void mainImage( out vec4 O, vec2 U )
{
	O = texture2D(iChannel0, U/iResolution.xy);
}

#define T .5

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;

    O-=O;

    if (mod(float(iFrame),5.) == 0. )  // do something only every 5 frames

        O = texture2D(iChannel1,U),    
        O *= smoothstep(T-.1, T, length(O-vec4(.05,.64,.15,1)));

    // U -= .001*vec2(1,0);
    O += (1.-O.a)* .995*texture2D(iChannel0,U);  // * ( iFrame==0 ? vec4(1) : texture() )
}
