/*
Based on https://www.shadertoy.com/view/4s33Rn from movAX13h 
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

	fragColor = texture2D(iChannel0, uv);
}

// Created by Stephane Cuillerdier - @Aiekick/2016
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

/*
Based on https://www.shadertoy.com/view/4s33Rn from movAX13h 
*/

float t = iGlobalTime;

// Farbrice Neyret 2  version 
float Invaders(vec2 p, float dec, float n, vec2 pattern)
{
    p += dec;
	p = mod(p,8.)-4.;
    p = vec2(abs(p.x),-floor(p.y));   
	return p.x<3.&&p.y<1.
    	&& mod(n/(exp2(2.5*(1.+sin(t/5e3))))  ,2.) > 1.
	    	&& mod(n/(exp2(floor(dot(p,pattern)))),2.) > 1.
	       		?  5. / dot(p+1., p+1.) : 0.;
}

/* original
float Invaders(vec2 p, float dec, float n, vec2 pattern)
{
	p += dec;
	p = mod(p,8.)-4.;
    p = vec2(abs(p.x),-floor(p.y));   
	float inv =  step(p.x, 3.) * step(p.y, 1.);
	inv *= step(1., floor(mod(n/(exp2(5.*(sin(t*0.0002)*0.5+0.5))),2.)));
	inv *= step(1., floor(mod(n/(exp2(floor(p.x * pattern.x + p.y * pattern.y))),2.)));
	inv *= 5. / dot(inv + p, inv + p);
	return inv;
}*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord * 0.25;

    float rand = fract(sin(dot(floor(p/8.0) ,vec2(9.09286,57.8924))) * 826.878);
	float n = 10e3 * rand * rand;

	vec2 pattern = vec2(1, ceil(3. + sin(t*8.) * 0.5 + 1.));

    float sha = Invaders(p, 1., n, pattern);
	float inv = Invaders(p, 0., n, pattern);

	vec3 color = vec3(0.2,0.4,0.8);

    vec3 col = (sha>0.5)?vec3(0):color;
    col = (inv>0.5)?color + inv:col;

	fragColor = vec4(col, 1) * 0.1 + texture2D(iChannel0, fragCoord / iResolution.xy * 1.) * 0.9;
}
