void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(uv, 0.5 + 0.5 * sin(iGlobalTime), 1.0);
    if (texture2D(iChannel0, uv).g < 0.5) {
        fragColor = vec4(1.0) - vec4(fragColor.xyz, 0.0);
    }
}

#define ON_COLOR vec4(0.0, 1.0, 0.0, 1.0)
#define OFF_COLOR vec4(0.0, 0.0, 0.0, 1.0)

bool isLive(in vec2 uv) {
    return texture2D(iChannel0, uv).g > 0.0;
}

int countNeighbors(in vec2 c) {
    int nc = 0;
    vec2 n;
    for (int x = -1; x <= 1; x++) {
        for (int y = -1; y <= 1; y++) {
            if (x != 0 || y != 0) {
                n.x = c.x + float(x);
                if (n.x < 0.0) {
                    n.x = iResolution.x;
                } else if (n.x >= iResolution.x) {
                    n.x = 0.0;
                }

                n.y = c.y + float(y);
                if (n.y < 0.0) {
                    n.y = iResolution.y;
                } else if (n.y >= iResolution.y) {
                    n.y = 0.0;
                }

                n /= iResolution.xy;

                if (isLive(n)) {
                    nc++;
                }
            }
        }
    }

    return nc;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    if (length(iMouse.xy - fragCoord.xy) < 2.0) {
        fragColor = ON_COLOR;
        return;
    }

    if (iGlobalTime < 1.0) {
        if (fragCoord.x > 50.0 &&
            fragCoord.x < 50.99 &&
            fragCoord.y > 49.0 &&
            fragCoord.y < 51.99) {
            fragColor = ON_COLOR;
        } else if (fragCoord.x > 51.0 &&
                   fragCoord.x < 51.99 &&
            	   fragCoord.y > 51.0 &&
                   fragCoord.y < 51.99) {
            fragColor = ON_COLOR;
        } else if (fragCoord.x > 49.0 &&
                   fragCoord.x < 49.99 &&
            	   fragCoord.y > 50.0 &&
                   fragCoord.y < 50.99) {
            fragColor = ON_COLOR;
        } else {
            fragColor = OFF_COLOR;
        }
    } else {
    	bool live = isLive(uv);
    	int nc = countNeighbors(fragCoord);
        if ((!live && nc == 3) ||
        (live && (nc == 2 || nc == 3))) {
        	fragColor = ON_COLOR;
    	} else {
	        fragColor = OFF_COLOR;
	    }
	}
}
