#define N 10. // N*N particles
#define R iResolution.xy
#define t iGlobalTime
#define id(v)    ( floor(v.x) + N*floor(v.y) )
#define point(P) texture2D(iChannel0,(P+.5)/R ).xy
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 O, vec2 U )
{
    O -= O;

    for (float j=0.; j<N; j++)
        for (float i=0.; i<N; i++) {
            vec2 p = ( U - point(vec2(i,j))  )/R.y;
            float id = id(vec2(i,j)), a = id/(N*N);
            p *= rot(id+t);
            O += (1.-O.a) * smoothstep(.1,.08,length(p))
                          * mix(texture2D(iChannel1,4.*p),texture2D(iChannel2,4.*p),.5+.5*cos(6.28*16.*a));
	       // O += 1e-5/dot(p,p);
        }
}

#define sticky 0.1  // the closer to 1 the more consistant the body

#define N 10. // N*N particles
#define R iResolution.xy
#define t iGlobalTime
#define id(v)    ( floor(v.x) + N*floor(v.y) )
#define slot(i)  ( vec2(mod(i,N), floor((i)/N))  )
#define point(P) texture2D(iChannel0, (P+.5)/R ).xy

void mainImage( out vec4 O, vec2 U )
{
    float i = id(U); // we should only consider u.x,u.y < N , but no harm.
	vec2 p = point(slot(i)), q = point(slot(i+1.));
    if (i==N*N) {
        q = iMouse.xy;    
        if (length(q)<1.) q = R*(1.+.8*cos(t*vec2(1.8,1))+.4*cos(t*vec2(4,5)))/2.; // fake mouse
    }

    if (length(p)==0.)  // initialization
       p = i/(N*N)*R+.5;
    else
		p += sticky*(q-p); // attracted by previous point

    O.xy = p;
}
