// Red buffer
#define ITERS 256

#define COUNT_INCR 0.0001

#define STOT 0.70710678118
#define STOT1P 1.70710678118
#define PIXEL_SCALE 1.5

#define MOD3 vec3(.1031,.11369,.13787)

/**
 *  The has function needs to be really good, because the more
 *  pixels we hit, the fuller the image. I'm no good at coming
 *  up with these, so I took this one from:
 *
 *  https://www.shadertoy.com/view/4djSRW
 */
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * MOD3);
	p3 += dot(p3, p3.yzx + 19.19);
	return fract(vec2((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y));
}

float orbitTrace(in vec2 c, in vec2 uv, in float pixelSize) {
    float incr = 1.0;
    float count = 0.0;
    vec2 z = c;
    float x;

    for (int i = 0; i < ITERS; i++) {
        if (z.x * z.x + z.y * z.y > 4.0) {
            return count;
        }
        x = z.x * z.x - z.y * z.y;
        z.y = 2.0 * z.x * z.y + c.y;
        z.x = x + c.x;

        if (length(z - uv) < pixelSize ||
            length(z - vec2(uv.x, -uv.y)) < pixelSize) {
            count += incr;
            incr *= 0.9; // Naturally decrease the intensity of "hot" areas
        }
    }
    return 0.0;
}

bool isInCardioid(vec2 c) {
    float p = sqrt(pow(c.x - (1.0 / 4.0), 2.0) + c.y * c.y);
    return c.x < p - 2.0 * p * p + (1.0 / 4.0);
}

bool isInPeriod2Bulb(vec2 c) {
    return pow(c.x + 1.0, 2.0) + c.y * c.y < (1.0 / 16.0);
}

bool isGoodPoint(vec2 c) {
    return !isInCardioid(c) && !(isInPeriod2Bulb(c));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = (fragCoord / iResolution.xy);
    vec2 uv = st;
    uv.x = uv.x * 3.0 - 2.0;
    uv.y = uv.y * 2.0 - 1.0;

    float pixelSize = max(1.0 / iResolution.x, 1.0 / iResolution.y);

    vec2 c = hash21(iGlobalTime);
    c = vec2(c.x * 3.0 - 2.0, c.y * 2.0 - 1.0);

    float ct = 0.0;
    if (isGoodPoint(c)) {
        ct = orbitTrace(c, uv, pixelSize * PIXEL_SCALE);
    }
    fragColor = texture2D(iChannel0, st);
    fragColor.x += ct * COUNT_INCR;
}

