// Antialiasing Buffer

// This can be tweaked once enough of the image has been
// generated
#define LOG_SCALE 500.0
#define POST_LOG_SCALE 8.0


#define STOT 0.70710678118
#define ONE_FIFTH (1.0 / 5.0)
#define ONE_HALF (1.0 / 2.0)
#define ONE_EIGHTH (1.0 / 8.0)

#define ANTI_ALIAS

vec3 buddhaSample(in vec2 uv) {
    return vec3(texture2D(iChannel0, uv).r,
                texture2D(iChannel1, uv).r,
                texture2D(iChannel2, uv).r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 dv = vec2(vec2(1.0) / iResolution.xy) * STOT;
    vec3 val;

    #ifndef ANTI_ALIAS
    val = buddhaSample(uv);
    #else
    vec3 color = vec3(0.0);

    color += ONE_HALF * buddhaSample(uv);
    color += ONE_EIGHTH * buddhaSample(vec2(uv.x + dv.x, uv.y));
    color += ONE_EIGHTH * buddhaSample(vec2(uv.x - dv.x, uv.y));
    color += ONE_EIGHTH * buddhaSample(vec2(uv.x, uv.y + dv.y));
    color += ONE_EIGHTH * buddhaSample(vec2(uv.x, uv.y - dv.y));

    val = color;
    #endif

    val = POST_LOG_SCALE * log(1.0 + val * LOG_SCALE);
	fragColor = vec4(val, 1.0);
}
