void mainImage( out vec4 f, vec2 v )
{
    f.xyz = iResolution;
	v = 2.*(2. * v - f.xy)/f.y;
    v = vec2(1.5,-.5) * (length(v) - iDate.w) 
        + atan(v.x, v.y) * 0.477;
	f.rgb = vec3(.09,.14,.22)/length(fract(v)-.5);
}

