void mainImage( out vec4 f, in vec2 g )
{
    vec2 s = iResolution.xy;
    vec2 q = g/s;
    f = texture2D(iChannel0, g / s);
   	f *= 0.5 + 0.5*pow( 10.* q.x * q.y * (1.0-q.x) * (1.0-q.y), 0.5 ); 
}

void mainImage( out vec4 f, in vec2 g )
{
    vec2 s = iResolution.xy;

    // central uv
	vec2 uv = 1.5*(2. * g - s)/s.y;

    // time
    float t = iGlobalTime;

    // radius piloted by sound
    vec3 soundRadius;
    soundRadius.x = texture2D(iChannel1, vec2(0.15,0.25)).r;
    soundRadius.y = texture2D(iChannel1, vec2(0.25,0.25)).r;
    soundRadius.z = texture2D(iChannel1, vec2(0.35,0.25)).r;
    soundRadius = (soundRadius - 0.5) / 0.5 + 1.5;
    //soundRadius = min(soundRadius, vec3(0.7));

    // path
    vec2 d0 = uv - vec2(cos(t), sin(t)) * soundRadius.x;
    vec2 d1 = uv - vec2(cos(t+2.09439), sin(t+2.09439)) * soundRadius.y;
    vec2 d2 = uv - vec2(cos(t+4.18879), sin(t+4.18879)) * soundRadius.z;


    // metaballs
    vec3 m = 0.01 / vec3(dot(d0,d0),dot(d1,d1),dot(d2,d2))/3.;
    m = 1. - (normalize(m) - dot(m,m) * m);

    // current color
	vec4 col = vec4(m,1);

    // last color
    vec4 bufA = texture2D(iChannel0, g / s);

    // simple blending of new and last color
    f = col * 0.02 + bufA * 0.98;

    // add some colored aura to the metaballs
    f += smoothstep(bufA, bufA+0.01, vec4(.8,.2,.5,1))*0.01;
}
