#define S 40.
                                                               // draw segment [a,b]
#define L(a,b)  O.g+= 2e-1 / length( clamp( dot(U-a,v=b-a)/dot(v,v), 0.,1.) *v - U+a )
#define T(i,j) texture2D(iChannel0,(.5+S*vec2(i,j))/iResolution.xy).xy

void mainImage( out vec4 O,  vec2 U )
{   
    O -= O; 

    for (int j=0; j<10; j++) 
    {
        vec2 v, P00, P01, P10 = T(0,j), P11 = T(0,j+1);

        for (int i=0; i<17; i++) 
        {
            P00=P10, P01=P11, P10 = T(i+1,j), P11=T(i+1,j+1);
            if (j<9 ) L(P00,P01);   // draw one vertical segment
            if (i<16) L(P00,P10);   // draw one horizontal segment
            O += smoothstep(5.,3.,length(U-T(i,j)));  // draw points
        }
    }
}

// each pixel of BufA encodes a pair of vertex coordinates.
// It's Image shader responsability to pick and connect some of these.


void mainImage( out vec4 O,  vec2 uv )
{
    vec2 U = uv/iResolution.xy;

    O = texture2D(iChannel0,U);
  //if (iFrame==0) {
    if (length(O.xy)==0.) {   // better if further increase of window size
        O.xy = uv + 10.*(2.*texture2D(iChannel1,U).xy-1.);  // initial jittered coords
        return;
    }
    else  {
        float a = 10.*iGlobalTime + uv.x+117.1*uv.y; // decorelates rotation angle
        O.xy += .2* vec2(cos(a),sin(a));       // shake coords
    }

    if ( length(iMouse.xy-O.xy) < 10. )        // edit coords
        O.xy = iMouse.xy;
}
