void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float c = texture2D( iChannel0, fragCoord / iResolution.xy ).x;

    fragColor = vec4(c,c,c,1.0);
}

// Created by inigo quilez - iq/2016
