float fetch( in vec2 p )
{
    return texture2D(iChannel0, fract(p/iChannelResolution[0].xy) ).x;
}

float hash1( float n )
{
    return fract(sin(n)*138.5453123);
}

void mainImage( out vec4 fragColor, in vec2 px )
{
    // box blur
    vec2 s;
    s.x = -0.5+2.0*fract(float(iFrame/1)/2.0);
    s.y = -0.5+2.0*fract(float(iFrame/2)/2.0);
    float k = texture2D( iChannel0, (px+s) / iResolution.xy ).x;


    // isolate band
    //float f = (k-.15)/0.053; f = exp( -f*f );
	float f = (k-.20)/0.044; f = exp( -f*f );
    //float f = (k-.30)/0.071; f = exp( -f*f );

    if( iFrame==0 ) f = hash1(px.x*13.0+hash1(px.y*71.1));

	fragColor = vec4( f, 0.0, 0.0, 0.0 );
}
