#define FRAME_INTERVAL 60

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 buf = texture2D(iChannel0, fragCoord / iResolution.xy);
    vec4 bri = texture2D(iChannel1, fragCoord / iResolution.xy);

    if (mod(float(iFrame), float(FRAME_INTERVAL)) < float(FRAME_INTERVAL) * 0.5) // init with britney
    {
        fragColor = bri * 0.9 + buf * 0.1;
    }
    else
    {

        fragColor = bri * 0.1 + buf * 0.9; // read buffer and keep britney
    }

}
