void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(texture2D(iChannel0, uv).rgb,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    if (fragCoord.x+0.5 == iResolution.x)
    {
        fragColor.r = texture2D(iChannel1, vec2(uv.y,0)).x;
        fragColor.g = texture2D(iChannel2, vec2(uv.y,0)).x;
        fragColor.b = texture2D(iChannel3, vec2(uv.y,0)).x;
    }
    else
    {
        fragColor.rgb = texture2D(iChannel0, (fragCoord+vec2(1,0))/iResolution.xy).rgb;
    }
}
