// by @paulofalcao
// Nostalgia moment, remake of somethingt I made a long time ago in DOS  :) 
// pf-blue.zip at http://ftp.scene.org/mirrors/hornet/demos/1997/b/

const float PI=3.14159265359;

vec2 rot(vec2 uv,float a){
	return vec2(uv.x*cos(a)-uv.y*sin(a),uv.y*cos(a)+uv.x*sin(a));
}

vec3 rotsim(vec2 p,float s){
	vec2 ret=p;
	ret=rot(p,-PI/(s*2.0));
	float pa=floor(atan(ret.x,ret.y)/PI*s)*(PI/s);
	ret=rot(p,pa);
	return vec3(ret.x,ret.y,pa);
}

float drawPoint(vec2 uv){
	return max(1.0-length(uv)*192.0,0.0);	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

	vec2 uv=iResolution.xy;uv=-.5*(uv-2.0*fragCoord.xy)/uv.x;

	//draw points
	uv=rot(uv,iGlobalTime);
	vec3 rs=rotsim(uv,16.0);
	uv=rs.xy;
	float f=drawPoint(uv-vec2(0,0.08+sin(rs.z*19.0+iGlobalTime*6.0)*0.03));
    vec3 dots = vec3(f*0.5,f*1.3,f*3.0);

    //draw lastframe
    uv=fragCoord.xy/iResolution.xy-0.5;
    uv*=0.8+sin(iGlobalTime*0.2)*0.2;
    uv+=0.5;
    vec3 back=texture2D(iChannel0,uv).xyz;

    //mix lastframe + points
	fragColor=vec4(back*0.8+dots,1.0);    

}
