// Tuned via XShade (http://www.funparadigm.com/xshade/)

void mainImage( out vec4 f, in vec2 g )
{
	f = texture2D(iChannel0, g/iResolution.xy);
}

// Created by Stephane Cuillerdier - @Aiekick/2015
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Tuned via XShade (http://www.funparadigm.com/xshade/)

void mainImage( out vec4 f, in vec2 g )
{
    vec2 
        s = iResolution.xy,
        u = (g+g-s)/s.y,
		ar = vec2(atan(u.x, u.y) / 3.14, length(u)) * 10.,
        v;

    float x = texture2D( iChannel1, vec2( floor(ar.y)/15., 0.25 ) ).x;

    vec3 col = mix(vec3(1,.3,0), vec3(.3,.8,.5), vec3(1.-floor(ar.y)*x)) ;

    ar.x += floor(ar.y) * x*.5;
    ar.x *= floor(ar.y) * 0.3;

	v = abs(fract(ar)-0.5);

	f.rgb = col + 0.6-0.025/dot(v, v);

    f.rgb = f.rgb * 0.1 + texture2D(iChannel0, g / s).rgb * 0.9;
}
