void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xy;

    float im = 0.5 + 0.5*sin(0.21*6.2831*iGlobalTime);

    vec3 col = mix( vec3(0.0), vec3(0.4), 1.0-smoothstep(im,im+0.01,p.y) );

	fragColor = vec4( col, 1.0 );
}

vec2 mainSound( float time )
{
    float f = 220.0;

    float im = 20.0 + 18.0*sin(0.21*6.2831*time);
    float y = sin( 6.2831*f*time + im*sin(0.25*6.2831*f*time) );

    y *= exp(-2.0*fract(8.0*time));

    return vec2(y);
}
