void mainImage( out vec4 f, in vec2 g )
{
    f = vec4(.8,.2,.5,1);

    vec2 s = iResolution.xy;

	vec4 h = texture2D(iChannel0, g / s * 0.995); // the magie is the 0.995 of s

    g = (g+g-s)/s.y*1.3;

    vec2 k = vec2(1.6,0) + iDate.w,a;

    float m;

    for (float i=0.;i<5.;i++)
    {   
        a = g - sin(k + 1.25*i);
        m += 0.02/dot(a,a);
    }

    f = m * 0.03 + h * 0.97 + step(h, f) * 0.01;
}
