/*
Elastic balls; there are also damping forces and gravity. The front-facing walls are
transparent when looking from outside, but not for the reflections. Use the mouse to
spin the box.
*/

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy
mat3 QToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

const float txRow = 64.;

vec4 Loadv4 (int idVar)
{
  float fi = float (idVar);
  return texture2D (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

const float pi = 3.14159;
const int nMol = 8;
vec3 pMol[nMol], ltDir, rdSign;
float dstFar, hbLen;
int idObj;
bool isRefl;

float ObjDf (vec3 p)
{
  vec4 fVec;
  vec3 q, eLen, eShift;
  float dMin, d, eWid, sLen;
  dMin = dstFar;
  sLen = hbLen - 0.35;
  eWid = 0.04;
  eShift = vec3 (0., sLen, sLen);
  eLen = vec3 (sLen + eWid, eWid, eWid);
  if (! isRefl) {
    fVec = vec4 (sLen * rdSign, 0.);
    q = p;
    d = min (min (PrBoxDf (q - fVec.xww, eLen.yxx),
       PrBoxDf (q - fVec.wyw, eLen.xyx)), PrBoxDf (q - fVec.wwz, eLen.xxy));
    if (d < dMin) { dMin = d;  idObj = 1; }
    q = abs (p);
    d = min (min (PrBoxDf (q - eShift, eLen), PrBoxDf (q - eShift.yxz, eLen.yxz)),
       PrBoxDf (q - eShift.yzx, eLen.yzx));
    if (d < dMin) { dMin = d;  idObj = 2; }
  } else {
    fVec = vec4 (sLen * vec3 (1.), 0.);
    q = abs (p);
    d = min (min (PrBoxDf (q - fVec.xww, eLen.yxx),
       PrBoxDf (q - fVec.wyw, eLen.xyx)), PrBoxDf (q - fVec.wwz, eLen.xxy));
    if (d < dMin) { dMin = d;  idObj = 1; }
  }
  for (int n = 0; n < nMol; n ++) {
    d = PrSphDf (p - pMol[n], 0.45);
    if (d < dMin) { dMin = d;  idObj = 3; }
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.0002, -0.0002, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 objCol;
  vec3 col, vn, w;
  float dstHit, reflFac;
  int idObjT;
  reflFac = 1.;
  isRefl = false;
  rdSign = sign (rd);
  for (int nf = 0; nf < 4; nf ++) {
    idObj = -1;
    dstHit = ObjRay (ro, rd);
    if (idObj == 3) {
      ro += rd * dstHit;
      rd = reflect (rd, ObjNf (ro));
      ro += 0.01 * rd;
      isRefl = true;
      reflFac *= 0.95;
    } else break;
  }
  if (dstHit < dstFar) {
    ro += rd * dstHit;
    idObjT = idObj;
    vn = ObjNf (ro);
    idObj = idObjT;
    if (idObj == 1) {
      w = smoothstep (0., 0.1, abs (fract (1.95 * ro + 0.5) - 0.5));
      objCol = vec4 (mix (vec3 (1., 1., 0.4), vec3 (0.5, 0.5, 1.),
         dot (abs (vn) * w.yzx * w.zxy, vec3 (1.))), 0.4);
    } else if (idObj == 2) objCol = vec4 (1., 1., 0.4, 0.4);
    else if (idObj == 3) objCol = vec4 (0.5, 0.5, 0.8, 0.1);
    col = objCol.rgb * (0.6 + 0.4 * max (dot (vn, ltDir), 0.)) +
       objCol.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 128.);
  } else col = vec3 (0., 0., 0.1);
  return clamp (reflFac * col, 0., 1.);
}

void GetMols ()
{
  for (int n = 0; n < nMol; n ++) pMol[n] = Loadv4 (2 * n).xyz;
  hbLen = Loadv4 (2 * nMol).y;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 qtVu;
  vec3 col, rd, ro;
  vec2 canvas, uv, ut;
  float tCur;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iGlobalTime;
  ut = abs (uv) - vec2 (1.);
  if (max (ut.x, ut.y) > 0.003) col = vec3 (0.82);
  else {
    dstFar = 100.;
    qtVu = Loadv4 (2 * nMol + 1);
    vuMat = QToRMat (qtVu);
    rd = normalize (vec3 (uv, 8.)) * vuMat;
    ro = vec3 (0., 0., -35.) * vuMat;
    ltDir = normalize (vec3 (1., 1.5, -1.2)) * vuMat;
    GetMols ();
    col = ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

// "Reflecting Balls" by dr2 - 2016
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 QMul (vec4 q1, vec4 q2)
{
  return vec4 (
     q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
     q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
   - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
   - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 EulToQ (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

float Hashff (float p)
{
  const float cHashM = 43758.54;
  return fract (sin (p) * cHashM);
}

const float txRow = 64.;

vec4 Loadv4 (int idVar)
{
  float fi = float (idVar);
  return texture2D (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  float fi = float (idVar);
  vec2 d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

const float pi = 3.14159;
const int nMol = 8;
vec4 qtVu;
float hbLen;

void Step (int mId, out vec3 r, out vec3 v)
{
  vec3 rn, vn, dr, f;
  float fOvlap, fDamp, grav, rSep, dt;
  fOvlap = 1000.;
  fDamp = 0.05;
  grav = 5.;
  r = Loadv4 (2 * mId).xyz;
  v = Loadv4 (2 * mId + 1).xyz;
  f = vec3 (0.);
  for (int n = 0; n < nMol; n ++) {
    rn = Loadv4 (2 * n).xyz;
    dr = r - rn;
    rSep = length (dr);
    if (n != mId && rSep < 1.) f += fOvlap * (1. / rSep - 1.) * dr;
  }
  dr = hbLen - abs (r);
  f -= step (dr, vec3 (1.)) * fOvlap * sign (r) * (1. / abs (dr) - 1.) * dr +
      vec3 (0., grav, 0.) * QToRMat (qtVu) + fDamp * v;
  dt = 0.02;
  v += dt * f;
  r += dt * v;
}

vec3 VInit (int n)
{
  float fn;
  fn = float (n);
  return 2. * normalize (vec3 (Hashff (fn), Hashff (fn + 0.3),
     Hashff (fn + 0.6)) - 0.5);
}

void OrientVu (inout vec4 qtVu, vec4 mPtr, inout vec4 mPtrP, bool init)
{
  vec3 vq1, vq2;
  vec2 dm;
  float mFac;
  if (! init) {
    qtVu = vec4 (0., 0., 0., 1.);
    mPtrP = vec4 (99., 0., -1., 0.);
  } else {
    if (mPtr.z > 0.) {
      if (mPtrP.x == 99.) mPtrP = mPtr;
      mFac = 1.5;
      dm = - mFac * mPtrP.xy;
      vq1 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      dm = - mFac * mPtr.xy;
      vq2 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      qtVu = normalize (QMul (vec4 (cross (vq1, vq2), dot (vq1, vq2)), qtVu));
      mPtrP = mPtr;
    } else mPtrP = vec4 (99., 0., -1., 0.);
  }
}

void Init (int mId, out vec3 r, out vec3 v)
{
  float fm, fme, fn;
  fme = 2.;
  fm = float (mId);
  r = 1.5 * floor (vec3 (mod (fm, fme), mod (fm, fme * fme) / fme,
     fm / (fme * fme))) - 0.5 * (fme - 1.);
  v = VInit (mId);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat;
  vec3 p, r, v;
  float tCur;
  int mId, pxId;
  bool doInit;
  vec2 kv = floor (fragCoord);
  pxId = int (kv.x + txRow * kv.y);
  if (kv.x >= txRow || pxId > 2 * nMol + 2) discard;
  tCur = iGlobalTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / iResolution.xy - 0.5;
  qtVu = Loadv4 (2 * nMol + 1);
  mPtrP = Loadv4 (2 * nMol + 2);
  mId = pxId / 2;
  doInit = false;
  if (iFrame < 2) {
    hbLen = 3.;
    OrientVu (qtVu, mPtr, mPtrP, false);
    stDat = vec4 (0., hbLen, tCur, 0.);
    if (mId < nMol) doInit = true;
  } else {
    OrientVu (qtVu, mPtr, mPtrP, true);
    stDat = Loadv4 (2 * nMol);
    ++ stDat.x;
    hbLen = stDat.y;
    if (mPtrP.z < 0.) qtVu = normalize (QMul (EulToQ (0.2 * (tCur - stDat.z) *
       pi * vec3 (-0.27, -0.34, -0.11)), qtVu));
    stDat.z = tCur;
    if (mId < nMol) {
      Step (mId, r, v);
      p = (2 * mId == pxId) ? r : v;
    }
  }
  if (doInit) {
    Init (mId, r, v);
    p = (2 * mId == pxId) ? r : v;
  }
  if (pxId == 2 * nMol + 1) stDat = qtVu;
  else if (pxId == 2 * nMol + 2) stDat = mPtrP;
  Savev4 (pxId, ((pxId < 2 * nMol) ? vec4 (p, 0.) : stDat), fragColor, fragCoord);
}
