void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 samp = texture2D(iChannel0,uv);

    fragColor = samp;
}

#define PROB_TREE 0.01
#define PROB_FIRE 0.00005
#define FIRE_TIME 1.0
#define GROWTH_TIME 1.0
#define BURN_THRESHOLD 0.7


float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame==0)
    {
        fragColor = vec4(0.0);
        return;
    }

    vec2 uv = fragCoord / ( iResolution.xy);
    vec4 current = texture2D(iChannel0, uv);   
    vec4 outVal = current;

    float prob = abs(rand(vec2(uv.x*iGlobalTime,uv.y*iGlobalTime)));

    if(current.x > 0.0)
    {
        outVal.x = max( 0.0, current.x - FIRE_TIME * iTimeDelta );
    }
    else if(current.y == 0.0 )
    {
        if(prob<PROB_TREE)
        {
            outVal.y = 0.25;
        }
    }
    else if(current.y == 1.0 )
    {
        if(prob<PROB_FIRE)
        {
            outVal.x = 1.0;
            outVal.y = 0.0;
        }
        else
        {
            vec4 neighborUp = texture2D(iChannel0, ( fragCoord + vec2(0.0,1.0) ) / (iResolution.xy));
    		vec4 neighborDown = texture2D(iChannel0, ( fragCoord + vec2(0.0,-1.0) ) / (iResolution.xy));
	  		vec4 neighborLeft = texture2D(iChannel0, ( fragCoord + vec2(1.0,0.0) ) / (iResolution.xy ));
     		vec4 neighborRight = texture2D(iChannel0, ( fragCoord + vec2(-1.0,0.0) ) / (iResolution.xy));


            if(neighborUp.x > BURN_THRESHOLD || neighborDown.x > BURN_THRESHOLD || neighborRight.x > BURN_THRESHOLD || neighborLeft.x > BURN_THRESHOLD )
            {
                outVal.x = 1.0;
                outVal.y = 0.0;
            }
        }

    }
    else if(current.y > 0.0 )
    {
        outVal.y = min(1.0,current.y + GROWTH_TIME * iTimeDelta );
    }

    fragColor = outVal;
}
