void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = 1. / iResolution.xy;
    vec2 uv = fragCoord.xy * pixelSize;

    if(uv.x > 1. - pixelSize.x){
    	fragColor = texture2D(iChannel0, uv.yx * vec2(1.,-1.) + vec2(0.,1.));
    }else{
        fragColor = texture2D(iChannel1, uv + vec2(1,0) * pixelSize);
    }
}
