#define speed 0.002
#define accel 10.0

float hash( vec2 p ) {
    float h = dot(p,vec2(127.1,311.7)); 
    return fract(sin(h)*43758.5453123);
}

vec2 pm(vec2 uv) {
    return mod(mod(uv, 1.0) + 1.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 is = texture2D(iChannel0, uv);
    vec2 pos = is.xy;
    vec2 texel = 1. / iResolution.xy;

    // 3x3 neighborhood coordinates
    float step_x = texel.x;
    float step_y = texel.y;
    vec2 n  = vec2(0.0, step_y);
    vec2 ne = vec2(step_x, step_y);
    vec2 e  = vec2(step_x, 0.0);
    vec2 se = vec2(step_x, -step_y);
    vec2 s  = vec2(0.0, -step_y);
    vec2 sw = vec2(-step_x, -step_y);
    vec2 w  = vec2(-step_x, 0.0);
    vec2 nw = vec2(-step_x, step_y);

    float im = texture2D(iChannel1, pm(pos)).x;
    float im_n = texture2D(iChannel1, pm(pos+n)).x;
    float im_e = texture2D(iChannel1, pm(pos+e)).x;
    float im_s = texture2D(iChannel1, pm(pos+s)).x;
    float im_w = texture2D(iChannel1, pm(pos+w)).x;
    float im_nw = texture2D(iChannel1, pm(pos+nw)).x;
    float im_sw = texture2D(iChannel1, pm(pos+sw)).x;
    float im_ne = texture2D(iChannel1, pm(pos+ne)).x;
    float im_se = texture2D(iChannel1, pm(pos+se)).x;

    float dx = 2.0 * (im_e - im_w) + im_ne + im_se - im_sw - im_nw;
    float dy = 2.0 * (im_n - im_s) + im_nw + im_ne - im_se - im_sw;

    vec2 vel = clamp(is.zw + accel * vec2(dx, dy), -1.0, 1.0);

    // initialize with noise
    if(iFrame<10) {
        fragColor = vec4(hash(uv + 1.5), hash(uv + 2.1), hash(uv + 3.5) - 0.5, hash(uv + 4.1) - 0.5);
    } else {
        fragColor = vec4(pm(pos + speed * vel), vel);
    }
}

#define NUM_PARTICLES 200

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 stepX = vec2(1.0 / iResolution.x, 0.0);
    vec2 halfStep = vec2(0.5 / iResolution.x, 0.5 / iResolution.x);

    float scale = 0.3;
    float accum = 0.0;

    vec2 c = fragCoord.xy;
    vec2 r = iResolution.xy;
    vec2 r_n = vec2(0.0, iResolution.y);
    vec2 r_e = vec2(iResolution.x, 0.0);

    for (int i = 0; i < NUM_PARTICLES; i++) {
        vec2 pos = r * texture2D(iChannel0, stepX * float(i) + halfStep).xy;

        float d = distance(c, pos);
        float d_n = distance(c + r_n, pos);
        float d_e = distance(c + r_e, pos);
        float d_s = distance(c - r_n, pos);
        float d_w = distance(c - r_e, pos);
        float minWrap = min(min(d_n, d_s), min(d_e, d_w));
        float minDistance = min(minWrap, d);
        accum += scale / minDistance;
    }
    fragColor = clamp(vec4(accum), 0.0, 1.0);

}
